import React, { useCallback, useContext, useEffect, useState } from 'react'
import Edit from "../img/edit.png"
import Delete from "../img/delete.png"
import { Link, useLocation, useNavigate } from 'react-router-dom'
import Menu from '../components/Menu'
import axios from 'axios'
import moment from 'moment'
import { AuthContext } from '../context/authContext';
import Bidding from '../components/Bidding.jsx'

const Single = () => {

const [product, setProduct] = useState({});
  const [timerDuration, setTimerDuration] = useState(0);
  const location = useLocation();
  const navigate = useNavigate();
  const product_id = location.pathname.split("/")[2];
  const [status, setStatus] = useState('unopened');
  const { currentUser } = useContext(AuthContext);
  const [countdown, setCountdown] = useState(null);
  const [biddingData, setBiddingData] = useState(null);

  const handleBiddingUpdate = useCallback((data) => {
    setBiddingData(data); // Update the state with the received data
  }, []);

  const startBidSession = useCallback(async () => {
    try {
      const before = await axios.get(`http://localhost:8800/api/orders/before/${product_id}`);
      if (before.data.status === '1') {
        setStatus('Sold');
        return;
      } else {
        await axios.post(`http://localhost:8800/api/orders/`, {
          user_id: currentUser.user_id,
          time: timerDuration,
          productId: product_id
        });
      }
    } catch (err) {
      console.log(err);
    }
  }, [currentUser.user_id, product_id, timerDuration]);

  const openBidding = useCallback(() => {
    setStatus('Bidding');
    const endTime = new Date();
    endTime.setSeconds(endTime.getSeconds() + parseInt(timerDuration));
    localStorage.setItem('endTime', endTime);
    setCountdown(timerDuration);
  }, [timerDuration]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      const remainingTime = calculateRemainingTime(new Date(localStorage.getItem('endTime')));
      if (remainingTime !== null) {
        setCountdown(remainingTime);
      } else {
        clearInterval(intervalId);
        setCountdown(null);
      }
    }, 1000);
    return () => clearInterval(intervalId);
  }, [timerDuration]);

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/products/${product_id}`);
        setProduct(res.data);
      } catch (err) {
        console.log("Error fetching data:", err);
      }
    };

    const fetchStatus = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/orders/before/${product_id}`);
        if (res.data.status === '1') setStatus('Sold');
      } catch (err) {
        console.log("Error fetching data:", err);
      }
    };

    fetchProductData();
    fetchStatus();
  }, [product_id]);

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8800/api/products/${product_id}`);
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  const startBiddingProcess = () => {
    startBidSession();
    openBidding();
  };

  const calculateRemainingTime = useCallback((endTime) => {
    const remainingTime = Math.floor((endTime.getTime() - new Date().getTime()) / 1000);
    return remainingTime > 0 ? remainingTime : null;
  }, []);

  const renderCountdown = () => {
    return countdown !== null ? <p>Time remaining: {countdown} seconds</p> : <p>Bidding ended</p>;
  };

  useEffect(() => {
    if (countdown === null) {
      setStatus('Ended');
    }
  }, [countdown]);
  // ended here
  if (!product || !currentUser || !product.user_id || !currentUser.user_id) {
    return <div>Login to see more...</div>; // or any other appropriate fallback content
  }
  // console.log("status", status)
  return (
    <div className='single'>
      <div className="content">
        <img src={product?.picture} alt='' />
        <div className='user'>
          <div className="info">
            <Link to={`/user/${product?.username}`}>
              <span>Uploaded by : {product?.username}</span>
            </Link>
            <p>Posted {moment(product.create_time).fromNow()}</p>
            <p>Starting at {product.price} $</p>
          </div>
          {currentUser.user_id === product.user_id && (

            <div className='edit'>

              {status !== 'Bidding' && (
                <div className='edit'>
                  <Link to={'/upload?edit=2'} state={product}>
                    <img src={Edit} alt='' />
                  </Link>
                  <img onClick={handleDelete} src={Delete} alt='' />
                </div>
              )}

              {/* modify this part so that a useeffect would return the status real time
              and hence this part could check the status to decide showing or not */}
              {status !== 'Sold' && status !== 'Bidding' && (
                <>
                  <input
                    type="number"
                    placeholder="Enter time in seconds"
                    value={timerDuration}
                    onChange={(e) => setTimerDuration(e.target.value)}
                  />
                  <button onClick={startBiddingProcess}>Open Bidding</button>
                </>
              )}

            </div>
          )}
        </div>
        <h1>{product.pname}</h1>
        {product.description}
        <div className="status">Status: {status} : {renderCountdown()}</div>
        {/* Bids Table */}
        {status !== 'unopened' && <Bidding product_id={product?.product_id} indicator={status} 
        user_id = {currentUser?.user_id} start_price = {product?.price} puser = {product?.user_id} 
        time = {timerDuration} onBiddingUpdate={handleBiddingUpdate}/>}
      </div>
      <Menu cat={product.prod_id} />
      {/* Status Indicator */}

    </div>
  )
}

export default Single;
