import React, { useState } from 'react'

const Chat = () => {
    console.log('enter chat frontend')
    const [query, setQuery ] = useState('')
    const [results, setResults ] = useState(null)
    const [isLoading, setIsLoading] = useState(false)
    const [error, setError] = useState(null)

    const handleQuerySubmit = async (event) => {

        event.preventDefault()
        setIsLoading(true)
        setError(null)

        try {
            console.log('enter generate sql query')
            const response = await fetch('http://localhost:8800/api/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            })

            if (!response.ok) {
                throw new Error(`Error: ${response.statusText}`)
            }

            if (response.headers.get("content-type")?.includes("application/json")) {
                const data = await response.json();
                setResults(data);
            } else {
                throw new Error("Did not receive JSON");
            }
            const data = await response.json()
            setResults(data)
            console.log('success generate sql query')
        } catch(err) {
            setError(err.message);
        } finally {
            setIsLoading(false)
        }
    }

    return (
        <div className="query">
            <form onSubmit={handleQuerySubmit}>
                <div className='input-wrapper'>
                    <textarea
                        type="text"
                        className="query-input"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Enter your query"
                    />
                </div>
                <button type="submit" className="query-submit" disabled={isLoading}>
                    {isLoading ? 'Loading...' : 'Submit'}
                </button>
            </form>
            {error && <div>Error: {error}</div>}
            {results && (
                <div>
                    <h2>Results:</h2>
                    <pre>{JSON.stringify(results, null, 2)}</pre>
                </div>
            )}
        </div>
    )
}

export default Chat
