import axios from 'axios';
import React, { useEffect, useState } from 'react'

const Bidding = ({ product_id, indicator }) => {
  // Assuming 'product_id' is an array of objects with bid details
  // For example: [{ bidder: 'User1', amount: 100, date: '2023-01-01' }, ...]
  const [bids, setBids] = useState([]);
  
  useEffect(()=>{
    const fetchBids =async () =>{
      try {
        const res = await axios.get(`http://localhost:8800/api/bids/${product_id}`)
        setBids(res.data)
      } catch(err) {
        console.error(err);
      }
    }
  },[])

  return (
    <div className="bids-table">
            <h3>Bids: </h3>
            <table>
                <thead>
                    <tr>
                        <th>Bidder</th>
                        <th>Amount</th>
                        <th>Date</th>
                    </tr>
                </thead>
                <tbody>
                    {bids.map((bid, index) => (
                        <tr key={index}>
                            <td>{bid.user_id}</td>
                            <td>${bid.price}</td>
                            <td>{bid.create_time}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Bidding;
