import React, { useState } from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // this is text editor
import { useLocation, useNavigate } from 'react-router-dom';
const Post = () => {
  const state = useLocation().state;
  const [value, setValue] = useState(state?.title || "");
  const [title, setTitle] = useState(state?.desc || "");
  const [file, setFile] = useState(null);
  const [cat, setCat] = useState(state?.cat || "");
  const navigate = useNavigate()

  return (
    <div className="add">
      <div className="content">
        <input
          type="text"
          placeholder="Title"
          onChange={(e) => setTitle(e.target.value)}
        />
        <div className="editorContainer">
          <ReactQuill
            className="editor"
            theme="snow"
            value={value}
            onChange={setValue}
          />
        </div>
      </div>
      <div className="menu">
        <div className="item">
          <h1>Publish</h1>
          <span>
            <b>Status: </b> Draft
          </span>
          <span>
            <b>Visibility: </b> Public
          </span>
          <input
            style={{ display: "none" }}
            type="file"
            id="file"
            name=""
            onChange={(e) => setFile(e.target.files[0])}
          />
          <label className="file" htmlFor="file">
            Upload Image
          </label>
          <div className="buttons">
            <button>Save as a draft</button>
            <button onClick={handleClick}>Publish</button>
          </div>
        </div>
        <div className="item">
          <h1>Category</h1>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "apl"}
              name="cat"
              value="apl"
              id="apl"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="apl">Appliances</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "app"}
              name="cat"
              value="app"
              id="app"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="app">Apparel</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "bks"}
              name="cat"
              value="bks"
              id="bks"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="bks">Books</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "elc"}
              name="cat"
              value="elc"
              id="elc"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="elc">Electronics</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "fod"}
              name="cat"
              value="fod"
              id="fod"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="fod">Food</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "food"}
              name="cat"
              value="food"
              id="food"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="food">Food</label>
          </div>
        </div>
      </div>
    </div>
  );
};

