import React, { useCallback, useContext, useEffect, useState } from 'react'
import Edit from "../img/edit.png"
import Delete from "../img/delete.png"
import { Link, useLocation, useNavigate } from 'react-router-dom'
import axios from 'axios'
import moment from 'moment'
import { AuthContext } from '../context/authContext';

const Order = () => {

  const location = useLocation();
  const order_id = location.pathname.split("/")[2];
  const { currentUser } = useContext(AuthContext);
  const [order, setOrder] = useState({});

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/users/order/${order_id}`);
        setOrder(res.data);
      } catch (err) {
        console.error(err);
        // Redirect or handle error
      }
    };

    fetchOrder();
  }, [order_id]);

  console.log(order);


  return (
      <div className="order-web">
        {order.map((order, index) => (
          <div key={index} className="order-card">
            <div><strong>Order ID:</strong> {order.order_id}</div>
            <div><strong>Bid Session ID:</strong> {order.bid_session_id}</div>
            <div><strong>Final Price:</strong> {order.final_price}</div>
            <div><strong>Buyer ID:</strong> {order.buyer_id}</div>
            <div><strong>Seller ID:</strong> {order.seller_id}</div>
            <div><strong>Seller Rate:</strong> {order.seller_rate}</div>
            <div><strong>Buyer Rate:</strong> {order.buyer_rate}</div>
            <div><strong>Create Time:</strong> {order.create_time}</div>
            <div><strong>End Time:</strong> {order.end_time}</div>
            <div><strong>ID:</strong> {order.id}</div>
            <div><strong>Ship Status:</strong> {order.ship_status}</div>
            <div><strong>Company:</strong> {order.company}</div>
            <div><strong>Pay Status:</strong> {order.pay_status}</div>
            <div><strong>Product ID:</strong> {order.product_id}</div>
          </div>
        ))}
      </div>
  );
}

export default Order
