import React, { useCallback, useContext, useEffect, useState } from 'react'
import Edit from "../img/edit.png"
import Delete from "../img/delete.png"
import { Link, useLocation, useNavigate } from 'react-router-dom'
import axios from 'axios'
import moment from 'moment'
import { AuthContext } from '../context/authContext';

const Order = () => {

  const location = useLocation();
  const order_id = location.pathname.split("/")[2];
  const { currentUser } = useContext(AuthContext);
  const [order, setOrder] = useState({});
  const [ratings, setRatings] = useState({});

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/users/order/${order_id}`);
        console.log(res);
        setOrder(res.data);
      } catch (err) {
        console.error(err);
        // Redirect or handle error
      }
    };

    fetchOrder();
  }, [order_id]);

  const truncateID = (id) => {
    if (id && id.length > 2) {
      return `${id.substring(0, 2)}***`; // Replace with asterisks
    }
    return id;
  };

  const handleRatingChange = (orderId, role, newRating) => {
    setRatings(prevRatings => ({
      ...prevRatings,
      [`${orderId}_${role}`]: newRating
    }));
  };

  const displayRating = (rate, orderId, role) => {
    if (rate === -1) {
      if ((role === 'buyer' && currentUser.user_id === order[orderId].seller_id) ||
        (role === 'seller' && currentUser.user_id === order[orderId].buyer_id)) {
        return (
          <input
            type="number"
            min="1"
            max="5"
            value={ratings[`${orderId}_${role}`] || ''}
            onChange={(e) => handleRatingChange(orderId, role, e.target.value)}
            placeholder="Rate 1-5"
          />
        );
      } else {
        return 'Unrated';
      }
    }
    return rate;
  };
  console.log(order)
  return (
    <div className="order-web">
      {order.map((order, index) => (
        (currentUser.user_id === order.buyer_id || currentUser.user_id === order.seller_id) ? (
          <div key={index} className="order-card">
            <div><strong>Order ID:</strong> {order.order_id}</div>
            <div><strong>Bid Session ID:</strong> {order.bid_session_id}</div>
            <div><strong>Final Price:</strong> {order.final_price}</div>
            <div><strong>Buyer ID:</strong> {truncateID(order.buyer_id)}</div>
            <div><strong>Seller ID:</strong> {truncateID(order.seller_id)}</div>
            <div><strong>Buyer Rate:</strong> {displayRating(order.buyer_rate, index, 'buyer')}</div>
            <div><strong>Seller Rate:</strong> {displayRating(order.seller_rate, index, 'seller')}</div>
            <div><strong>Ship Status:</strong> {order.ship_status}</div>
            <div><strong>Ship Company:</strong> {order.company}</div>
            <div><strong>Payment ID:</strong> {order.id}</div>
            <div><strong>Pay Status:</strong> {order.pay_status}</div>
            <div><strong>Product ID:</strong> {order.product_id}</div>
          </div>
        ) : (
          <div key={index} className="order-message">
            Only your order is visible
          </div>
        )
      ))}
    </div>
  );
}

export default Order
